import React, { Component } from "react";
import { connect } from "react-redux";

import { BrowserRouter, Route, Switch, withRouter } from "react-router-dom";

import Home from "./components/pages/home";
import About from "./components/pages/about";
import Portfolio from "./components/pages/portfolio";
import Contact from "./components/pages/contact";
import Error from "./components/pages/errorpage";

import Navigation from "./components/layout/navigation";

import { PageTurn } from "./store/actions/pageactions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectApp extends Component {
  defineFirstpage = page => {
    this.props.dispatch(PageTurn());
  };
  pageturn = () => {
    document.getElementsByTagName("body")[0].style.overflow = "hidden";
    this.refs.display.classList.add("pagegoingup");
    setTimeout(() => {
      this.refs.display.classList.remove("pagegoingup");
      this.refs.display.classList.add("pagegoingdown");
      setTimeout(() => {
        this.refs.display.classList.remove("pagegoingdown");
        document.getElementsByTagName("body")[0].style.overflow = "";
      }, 1000);
    }, 1000);
  };
  render() {
    const RoutedNavigation = withRouter(props => <Navigation {...props} />);
    return (
      <div className="App">
        <div className="loading" />
        <BrowserRouter>
          <div ref="display" className="display">
            <RoutedNavigation pageturn={this.pageturn} />
            <Switch>
              <Route path="/" component={Home} exact />
              <Route path="/about" component={About} />
              <Route path="/portfolio" component={Portfolio} />
              <Route path="/contact" component={Contact} />
              <Route component={Error} />
            </Switch>
          </div>
        </BrowserRouter>
      </div>
    );
  }
}

const App = connect(mapStateToProps)(ToConnectApp);

export default App;
