import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";

import { PageTurn, FirstPageLoaded } from "../../store/actions/pageactions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectNavigation extends Component {
  componentDidMount() {
    if (!this.props.store.page.firstpageloaded) {
      let firstpage = this.props.location.pathname.split("/")[1];
      if (firstpage === "") {
        firstpage = "home";
      }
      this.props.dispatch(PageTurn(firstpage));
      this.props.dispatch(FirstPageLoaded());
    }
  }
  render() {
    return (
      <div className="navigation">
        <nav>
          <div className="nav__container">
            <div className="buttons">
              <button onClick={() => this.props.navigate("home", 0)}>
                Home
              </button>
              <button onClick={() => this.props.navigate("about", 1)}>
                About
              </button>
              <button onClick={() => this.props.navigate("portfolio", 2)}>
                Portfolio
              </button>
              <button onClick={() => this.props.navigate("contact", 3)}>
                Contact
              </button>
            </div>
            <div className="nav__title">
              <h1>AMAN KUMAR</h1>
            </div>
            <div className="social__links">
              <a
                href="https://www.linkedin.com/in/aman-kumar-183035162/"
                target="_blank"
                rel="noopener noreferrer"
                title="LinkedIn"
              >
                <i className="fab fa-linkedin" />
              </a>
              <a
                href="https://www.twitter.com/amantheroot"
                target="_blank"
                rel="noopener noreferrer"
                title="Twitter"
              >
                <i className="fab fa-twitter" />
              </a>
              <a
                href="https://www.github.com/amantheroot"
                target="_blank"
                rel="noopener noreferrer"
                title="GitHub"
              >
                <i className="fab fa-github" />
              </a>
              <a
                href="mailto:amantheroot@gmail.com"
                target="_blank"
                rel="noopener noreferrer"
                title="Mail Me"
              >
                <i className="fa fa-envelope" />
              </a>
            </div>
          </div>
        </nav>

        <div className="links">
          <NavLink to="/" />
          <NavLink to="/about" />
          <NavLink to="/portfolio" />
          <NavLink to="/contact" />
        </div>
      </div>
    );
  }
}

const Navigation = connect(mapStateToProps)(ToConnectNavigation);

export default Navigation;
