import React, { Component } from "react";
import BrainTemplate from "./braintemplate";
import BrainParts from "./brainparts";
import SkillsInfo from "./skillsinfo";

class Skills extends Component {
  state = {
    size: 400,
    direc: 0.5
  };
  componentDidMount() {
    let size = this.refs.skills.childNodes[0].childNodes[0].childNodes[0]
      .offsetHeight;
    this.setState({ size });

    this.refs.skills.childNodes[0].childNodes[0].style.marginTop = 0 + "px";
    this.refs.skills.childNodes[0].childNodes[1].style.marginTop = 0 + "px";

    window.updownbrain = setInterval(this.BrainAnimate, 1000 / 15);
  }
  BrainAnimate = () => {
    let braintemplate = this.refs.skills.childNodes[0].childNodes[0];
    let brainparts = this.refs.skills.childNodes[0].childNodes[1];

    let mtop = Number.parseInt(braintemplate.style.marginTop.split("px")[0]);

    if (mtop >= 8 || mtop <= -8) {
      this.setState({ direc: -this.state.direc });
    }

    mtop += this.state.direc;

    console.log(mtop);

    braintemplate.style.marginTop = mtop + "px";
    brainparts.style.marginTop = mtop + "px";
  };
  handleMouseOver = (id, frominfo) => {
    let infos = [
      ...this.refs.skills.childNodes[0].childNodes[2].childNodes[0].childNodes
    ];
    infos.splice(0, 2);

    let brainparts = [
      ...this.refs.skills.childNodes[0].childNodes[1].childNodes[0]
        .childNodes[0].childNodes
    ];

    let index = id;
    if (id === 0 || id === 1) {
      index = id + 4;
    } else if (id === 4 || id === 5) {
      index = id - 4;
    }
    if (frominfo) {
      infos[id].style.opacity = 1;
      brainparts[index].style.fillOpacity = 0.6;
    } else {
      infos[index].style.opacity = 1;
      brainparts[id].style.fillOpacity = 0.6;
      this.ShiftBrain(id);
    }
  };
  handleMouseOut = (id, frominfo) => {
    let infos = [
      ...this.refs.skills.childNodes[0].childNodes[2].childNodes[0].childNodes
    ];
    infos.splice(0, 2);

    let brainparts = [
      ...this.refs.skills.childNodes[0].childNodes[1].childNodes[0]
        .childNodes[0].childNodes
    ];

    let index = id;
    if (id === 0 || id === 1) {
      index = id + 4;
    } else if (id === 4 || id === 5) {
      index = id - 4;
    } else {
      index = id;
    }
    if (frominfo) {
      infos[id].style.opacity = 0.3;
      brainparts[index].style.fillOpacity = 0;
    } else {
      infos[index].style.opacity = 0.3;
      brainparts[id].style.fillOpacity = 0;
      this.ShiftBrainBack();
    }
  };
  ShiftBrain = id => {
    clearInterval(window.updownbrain);
    let braintemplate = this.refs.skills.childNodes[0].childNodes[0];
    let brainparts = this.refs.skills.childNodes[0].childNodes[1];

    let mt = 0;
    let ml = 0;
    let m = 5;
    switch (id) {
      case 0:
        mt = -m;
        ml = m;
        break;
      case 1:
        mt = -m;
        ml = -m;
        break;
      case 2:
        ml = m;
        break;
      case 3:
        ml = -m;
        break;
      case 4:
        mt = m;
        ml = m;
        break;
      case 5:
        mt = m;
        ml = -m;
        break;
      default:
        break;
    }
    braintemplate.style.marginTop = mt + "px";
    braintemplate.style.marginLeft = ml + "px";

    brainparts.style.marginTop = mt + "px";
    brainparts.style.marginLeft = ml + "px";
  };
  ShiftBrainBack = () => {
    let braintemplate = this.refs.skills.childNodes[0].childNodes[0];
    let brainparts = this.refs.skills.childNodes[0].childNodes[1];

    braintemplate.style.marginTop = 0 + "px";
    braintemplate.style.marginLeft = 0 + "px";

    brainparts.style.marginTop = 0 + "px";
    brainparts.style.marginLeft = 0 + "px";
    setTimeout(() => {
      window.updownbrain = setInterval(this.BrainAnimate, 1000 / 60);
    }, 500);
  };
  render() {
    return (
      <div ref="skills" className="skills">
        <div className="brain">
          <div className="brain__template">
            <BrainTemplate />
          </div>
          <div className="brain__parts">
            <BrainParts
              onMouseOver={this.handleMouseOver}
              onMouseOut={this.handleMouseOut}
              size={this.state.size}
            />
          </div>
          <div className="skills__info">
            <SkillsInfo
              onMouseOver={this.handleMouseOver}
              onMouseOut={this.handleMouseOut}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Skills;
