// import { combineReducers } from "redux";

// export default combineReducers({});

import InitialState from "../initialState";

export default function reducer(state = InitialState, action) {
  switch (action.type) {
    case "toggleGamePlay":
      return { ...state, game: !state.game };
    case "toggleBarReady":
      return { ...state, barReady: !state.barReady };
    case "ChangeVel":
      return { ...state, ballv: state.ballv * action.payload };
    case "ChangeYVel":
      return { ...state, ballyv: state.ballyv * action.payload };
    case "ChangeXVel":
      return { ...state, ballxv: state.ballxv * action.payload };
    case "ChangeCoords":
      return { ...state, ballx: action.payload.x, bally: action.payload.y };
    case "BrickHit":
      let bricks = [...state.bricks];
      bricks[action.payload].alive = false;
      return { ...state, bricks };
    default:
      return state;
  }
}
