import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectNavigation extends Component {
  componentDidMount() {}
  onClick = page => {
    let links = this.refs.nav.childNodes[1].childNodes;
    links.forEach(link => link.classList.remove("active"));
    let index;
    switch (page) {
      case "home":
        index = 0;
        break;
      case "about":
        index = 1;
        break;
      case "portfolio":
        index = 2;
        break;
      case "contact":
        index = 3;
        break;
      default:
        index = 0;
        break;
    }
    window.pageturn = setTimeout(() => {
      links[index].click();
      console.log(links);
    }, 1000);
  };
  render() {
    return (
      <div ref="nav" className="navigation">
        <div className="buttons">
          <button onClick={() => this.onClick("home")}>Home</button>
          <button onClick={() => this.onClick("about")}>About</button>
          <button onClick={() => this.onClick("portfolio")}>Portfolio</button>
          <button onClick={() => this.onClick("contact")}>Contact</button>
        </div>
        <div className="links">
          <NavLink to="/" />
          <NavLink to="/about" />
          <NavLink to="/portfolio" />
          <NavLink to="/contact" />
        </div>
      </div>
    );
  }
}

const Navigation = connect(mapStateToProps)(ToConnectNavigation);

export default Navigation;
