import React, { Component } from "react";

class Home extends Component {
  state = {
    A: 1,
    B: 1,
    C1: 1,
    C2: 1
  };
  componentDidMount() {
    console.log("Home Loaded");
    this.skewName(30);
    this.animateTitle();

    let letters = [...this.refs.name.childNodes[0].childNodes].concat([
      ...this.refs.name.childNodes[1].childNodes
    ]);

    letters.forEach(letter => {
      letter.addEventListener("mouseover", () => {
        letter.style.transform = "skewX(0deg)";
      });
      letter.addEventListener("mouseout", () => {
        this.skewName(30);
      });
    });
  }
  skewName = degrees => {
    let letters = [...this.refs.name.childNodes[0].childNodes].concat([
      ...this.refs.name.childNodes[1].childNodes
    ]);
    letters.forEach((letter, index) => {
      let id = index;
      if (index >= letters.length / 2) {
        id = index + 1;
      }
      let deg = -degrees + id * (degrees / (letters.length / 2));
      letter.style.transform = "skewX(" + deg + "deg)";
    });
  };
  animateTitle = () => {
    let titles = [
      "A Front End Web Developer",
      "A Guy Who Builds Websites And Web Applications",
      "A Guy Who Designs Websites And Web Applications",
      "A Guy Who Still Enjoys Video Games From The 2000's",
      "A Guy Who Can Help You Build Your Next Project."
    ];
    let firstSquishText = "A";
    this.refs.title.innerHTML = "A";
    let firstWidth = this.refs.title.offsetWidth;
    this.refs.title.innerHTML = "A Guy Who";
    let secondWidth = this.refs.title.offsetWidth;

    this.refs.title.innerHTML = titles[0];
    this.refs.title.style.width = "100%";

    setTimeout(() => {
      this.squishTitles(firstWidth, secondWidth, titles, true);
    }, 3000);
  };
  squishTitles = (
    firstWidth,
    secondWidth,
    titles,
    firstTime = false,
    transTime = 3000,
    breakTime = 3000,
    pauseTime = 1000
  ) => {
    if (firstTime) {
      this.refs.title.style.width = firstWidth + "px";
      setTimeout(() => {
        this.refs.title.innerHTML = titles[1];
        this.refs.title.style.width = "100%";
        setTimeout(() => {
          this.squishSeconds(firstWidth, secondWidth, titles, 2);
        }, transTime + breakTime);
      }, transTime + pauseTime);
    } else {
      this.refs.title.style.width = firstWidth + "px";
      setTimeout(() => {
        this.refs.title.innerHTML = titles[0];
        this.refs.title.style.width = "100%";
        setTimeout(() => {
          this.refs.title.style.width = firstWidth + "px";
          setTimeout(() => {
            this.refs.title.innerHTML = titles[1];
            this.refs.title.style.width = "100%";
            setTimeout(() => {
              this.squishSeconds(firstWidth, secondWidth, titles, 2);
            }, transTime + breakTime);
          }, transTime + pauseTime);
        }, transTime + breakTime);
      }, transTime + pauseTime);
    }
  };
  squishSeconds = (
    firstWidth,
    secondWidth,
    titles,
    id,
    transTime = 3000,
    breakTime = 3000,
    superBreakTime = 3000,
    pauseTime = 1000
  ) => {
    this.refs.title.style.width = secondWidth + "px";
    setTimeout(() => {
      this.refs.title.innerHTML = titles[id];
      this.refs.title.style.width = "100%";
      setTimeout(() => {
        if (id >= titles.length - 1) {
          setTimeout(() => {
            this.squishTitles(firstWidth, secondWidth, titles);
          }, superBreakTime);
        } else {
          this.squishSeconds(firstWidth, secondWidth, titles, id + 1);
        }
      }, transTime + breakTime);
    }, transTime + pauseTime);
  };
  render() {
    return (
      <div className="home">
        <h1 className="hello">Hello, I'm</h1>
        <h1 ref="name" className="name">
          <div className="fname">
            <div>A</div>
            <div>M</div>
            <div>A</div>
            <div>N</div>
            <div> </div>
          </div>
          <div className="lname">
            <div>K</div>
            <div>U</div>
            <div>M</div>
            <div>A</div>
            <div>R</div>
          </div>
        </h1>
        <h1 ref="title" className="title">
          A Front End Web Developer
        </h1>
        <button onClick={() => this.props.navigate("about", 1)}>
          Let's Begin
        </button>
      </div>
    );
  }
}

export default Home;
