import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";

import { PageTurn, FirstPageLoaded } from "../../store/actions/pageactions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectNavigation extends Component {
  componentDidMount() {
    if (!this.props.store.page.firstpageloaded) {
      let firstpage = this.props.location.pathname.split("/")[1];
      if (firstpage === "") {
        firstpage = "home";
      }
      this.props.dispatch(PageTurn(firstpage));
      this.props.dispatch(FirstPageLoaded());
    }
  }
  onClick = (page, index) => {
    this.props.navigate(page, index);
  };
  render() {
    return (
      <div ref="nav" className="navigation">
        <div className="buttons">
          <button onClick={() => this.props.navigate("home", 0)}>Home</button>
          <button onClick={() => this.props.navigate("about", 1)}>About</button>
          <button onClick={() => this.props.navigate("portfolio", 2)}>
            Portfolio
          </button>
          <button onClick={() => this.props.navigate("contact", 3)}>
            Contact
          </button>
        </div>
        <div className="links">
          <NavLink to="/" />
          <NavLink to="/about" />
          <NavLink to="/portfolio" />
          <NavLink to="/contact" />
        </div>
      </div>
    );
  }
}

const Navigation = connect(mapStateToProps)(ToConnectNavigation);

export default Navigation;
