import React, { Component } from "react";
import { connect } from "react-redux";

import { toggleGameplay } from "../store/actions/actions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectForm extends Component {
  componentDidMount() {
    this.refs.game.addEventListener("mousemove", e => {
      this.handleMouseMove(e);
    });
  }
  handleMouseMove = e => {
    console.log(e.clientX);
    if (e.clientX >= 115 && e.clientX <= window.innerWidth - 115) {
      this.refs.bar.style.left = e.clientX + "px";
    } else if (e.clientX < 115) {
      this.refs.bar.style.left = "115px";
    } else if (e.clientX > window.innerWidth - 115) {
      this.refs.bar.style.left = window.innerWidth - 115 + "px";
    }
  };
  toggleGameplay = () => {
    this.props.dispatch(toggleGameplay());
  };
  render() {
    return (
      <div
        className={this.props.store.game ? "form breakout" : "form"}
        ref="game"
      >
        <h1>Now Enough About Me...</h1>
        <form>
          <div className="inputs">
            <input type="text" placeholder="First Name" />
            <input type="text" placeholder="Last Name" />
            <input type="email" placeholder="Your Email" />
            <input type="text" placeholder="Subject" />
          </div>
          <div className="gamearea">
            <div className="input__block">
              <textarea placeholder="Your Lovely Message Goes Here :)" />
            </div>
            {/* <div
              class="g-recaptcha"
              data-sitekey="6LdC230UAAAAAHrGqrEH6yvebju1qIz3XxF4jsfW"
            /> */}
            <div className="input__bar">
              <div ref="bar" className="bar" />
              <button>Submit</button>
            </div>
          </div>
        </form>
        <div className="toggleGame">
          <button onClick={this.toggleGameplay}>Game!</button>
        </div>
      </div>
    );
  }
}

const Form = connect(mapStateToProps)(ToConnectForm);

export default Form;
