import React, { Component } from "react";

class Home extends Component {
  componentDidMount() {
    this.skewName(30);

    let letters = [...this.refs.name.childNodes[0].childNodes].concat([
      ...this.refs.name.childNodes[1].childNodes
    ]);

    letters.forEach(letter => {
      letter.addEventListener("mouseover", () => {
        letter.style.transform = "skewX(0deg)";
      });
      letter.addEventListener("mouseout", () => {
        this.skewName(30);
      });
    });
  }
  skewName = degrees => {
    let letters = [...this.refs.name.childNodes[0].childNodes].concat([
      ...this.refs.name.childNodes[1].childNodes
    ]);
    letters.forEach((letter, index) => {
      let id = index;
      if (index >= letters.length / 2) {
        id = index + 1;
      }
      let deg = -degrees + id * (degrees / (letters.length / 2));
      letter.style.transform = "skewX(" + deg + "deg)";
    });
  };
  render() {
    return (
      <div className="home">
        <h1 className="hello">Hello, my name is</h1>
        <h1 ref="name" className="name">
          <div className="fname">
            <div>A</div>
            <div>M</div>
            <div>A</div>
            <div>N</div>
            <div> </div>
          </div>
          <div className="lname">
            <div>K</div>
            <div>U</div>
            <div>M</div>
            <div>A</div>
            <div>R</div>
          </div>
        </h1>
        <div ref="title" className="title" />
        <button onClick={() => this.props.navigate("about", 1)}>
          Let's Begin
        </button>
      </div>
    );
  }
}

export default Home;
