// import { combineReducers } from "redux";

// export default combineReducers({});

import InitialState from "../initialState";

export default function reducer(state = InitialState, action) {
  switch (action.type) {
    case "toggleGamePlay":
      return { ...state, game: !state.game };
    default:
      return state;
  }
}
