import React, { Component } from "react";
import { NavLink } from "react-router-dom";
import { connect } from "react-redux";

import { PageTurn } from "../../store/actions/pageactions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectNavigation extends Component {
  componentDidMount() {
    console.log("Nav Loaded");
    let firstpage = this.props.location.pathname.split("/")[1];
    if (firstpage === "") {
      firstpage = "home";
    }
    this.props.definefirstpage(firstpage);
  }
  onClick = page => {
    let currpage = this.props.store.page.page;

    const PT = () => {
      if (currpage !== page) {
        let buttons = this.refs.nav.childNodes[0].childNodes;
        buttons.forEach(btn => (btn.disabled = true));

        let links = this.refs.nav.childNodes[1].childNodes;
        links.forEach(link => link.classList.remove("active"));

        let index;
        switch (page) {
          case "home":
            index = 0;
            break;
          case "about":
            index = 1;
            break;
          case "portfolio":
            index = 2;
            break;
          case "contact":
            index = 3;
            break;
          default:
            index = 0;
            break;
        }

        this.props.pageturn();

        window.pageturn = setTimeout(() => {
          this.props.dispatch(PageTurn(page));
          links[index].classList.add("active");
          links[index].click();

          window.returnbtns = setTimeout(() => {
            buttons.forEach(btn => (btn.disabled = false));
          }, 1000);
        }, 1000);
      }
    };

    if (currpage === "contact" && this.props.store.form.infoprovided) {
      let confirmed = window.confirm(
        `If You Continue, All Your Inputs Will Be Erased.\nIs That OK?`
      );
      if (confirmed) {
        PT();
      }
    } else {
      PT();
    }
  };
  render() {
    return (
      <div ref="nav" className="navigation">
        <div className="buttons">
          <button onClick={() => this.onClick("home")}>Home</button>
          <button onClick={() => this.onClick("about")}>About</button>
          <button onClick={() => this.onClick("portfolio")}>Portfolio</button>
          <button onClick={() => this.onClick("contact")}>Contact</button>
        </div>
        <div className="links">
          <NavLink to="/" />
          <NavLink to="/about" />
          <NavLink to="/portfolio" />
          <NavLink to="/contact" />
        </div>
      </div>
    );
  }
}

const Navigation = connect(mapStateToProps)(ToConnectNavigation);

export default Navigation;
