import React, { Component } from "react";

class Home extends Component {
  state = {
    A: 1,
    B: 1,
    C1: 1,
    C2: 1
  };
  componentDidMount() {
    console.log("Home Loaded");
    this.skewName(30);
    this.animateTitle(100000000);

    let letters = [...this.refs.name.childNodes[0].childNodes].concat([
      ...this.refs.name.childNodes[1].childNodes
    ]);

    letters.forEach(letter => {
      letter.addEventListener("mouseover", () => {});
    });
  }
  skewName = degrees => {
    let letters = [...this.refs.name.childNodes[0].childNodes].concat([
      ...this.refs.name.childNodes[1].childNodes
    ]);
    letters.forEach((letter, index) => {
      let id = index;
      if (index >= letters.length / 2) {
        id = index + 1;
      }
      let deg = -degrees + id * (degrees / (letters.length / 2));
      letter.style.transform = "skewX(" + deg + "deg)";
    });
  };
  animateTitle = transitionTime => {
    let titles = [
      "A Front End Web Developer",
      "A Guy Who Builds Websites",
      "A Guy Who Builds Web Applications",
      "A Guy Who Designs Websites",
      "A Guy Who Designs Web Applications",
      "A Guy Who Still Enjoys Video Games From The 2000's"
    ];
    titles.forEach((title, id) => {
      setTimeout(() => {
        this.refs.title.innerHTML = title;
      }, id * transitionTime);
    });
    setTimeout(() => {
      this.animateTitle(transitionTime);
    }, titles.length * transitionTime);
  };
  render() {
    return (
      <div className="home">
        <h1 className="hello">Hello, I'm</h1>
        <h1 ref="name" className="name">
          <div className="fname">
            <div>A</div>
            <div>M</div>
            <div>A</div>
            <div>N</div>
            <div> </div>
          </div>
          <div className="lname">
            <div>K</div>
            <div>U</div>
            <div>M</div>
            <div>A</div>
            <div>R</div>
          </div>
        </h1>
        <h1 ref="title" className="title">
          A Front End Web Developer
        </h1>
        <button onClick={() => this.props.navigate("about", 1)}>
          Let's Begin
        </button>
      </div>
    );
  }
}

export default Home;
