import React, { Component } from "react";
import { connect } from "react-redux";

import {
  toggleGameplay,
  toggleBarReady,
  ChangeCoords,
  ChnageVel,
  ChnageYVel,
  ChnageXVel,
  BrickHit,
  ToggleHit
} from "../store/actions/actions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectForm extends Component {
  componentDidMount() {
    this.refs.game.addEventListener("mousemove", e => {
      this.handleMouseMove(e);
    });
  }
  handleMouseMove = e => {
    if (this.props.store.barReady) {
      if (e.clientX >= 115 && e.clientX <= window.innerWidth - 115) {
        this.refs.bar.style.left = e.clientX + "px";
      } else if (e.clientX < 115) {
        this.refs.bar.style.left = 115 + "px";
      } else if (e.clientX > window.innerWidth - 115) {
        this.refs.bar.style.left = window.innerWidth - 115 + "px";
      }
    }
  };
  toggleGameplay = () => {
    if (!this.props.store.game) {
      setTimeout(() => {
        this.drawball();
      }, 1000);
      this.props.dispatch(toggleGameplay());
      this.refs.toggleGame.disabled = true;
      setTimeout(() => {
        this.refs.block.style.backgroundColor = "black";
        this.props.dispatch(toggleBarReady());
        this.refs.toggleGame.disabled = false;

        window.moving = setInterval(this.ballMove, 1000 / 60);
      }, 4000);
    } else {
      this.props.dispatch(toggleBarReady());

      this.refs.bar.style.transition = "opacity 1s, left 1s";
      this.refs.bar.style.left = window.innerWidth / 2 + "px";

      this.refs.block.style.backgroundColor = "white";

      this.refs.block.childNodes.forEach(brick => {
        brick.classList.add("bricktrans");
      });

      this.refs.toggleGame.disabled = true;

      setTimeout(() => {
        this.ballStop();

        this.refs.bar.style.transition = "opacity 1s";
        this.refs.block.childNodes.forEach(brick => {
          brick.classList.remove("bricktrans");
        });

        this.props.dispatch(toggleGameplay());
        this.refs.toggleGame.disabled = false;
        this.refs.canvas.style.zIndex = "-1";
      }, 1000);
    }
  };
  drawball = () => {
    let canvas = this.refs.canvas;
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    let ctx = canvas.getContext("2d");
    let left = canvas.width / 2 - 12;
    let top = canvas.height - 75;
    ctx.fillStyle = "#484EDA";
    ctx.fillRect(left, top, 24, 24);

    let up = 1;
    let uptop = top;
    window.ballDebu = setInterval(() => {
      if (uptop > top - 24) {
        ctx.clearRect(0, 0, canvas.width, canvas.height);
        uptop = top - up;
        ctx.fillRect(left, uptop, 24, 24);
        up++;
      } else {
        clearTimeout(window.ballDebu);
        this.refs.canvas.style.zIndex = "1";
      }
    }, 1000 / 15);
  };
  ballMove = () => {
    let canvas = this.refs.canvas;
    let ctx = canvas.getContext("2d");

    let bleft = this.props.store.ballx;
    let btop = this.props.store.bally;
    let bv = this.props.store.ballv;
    window.backupv = bv;
    let bxv = this.props.store.ballxv;
    let byv = this.props.store.ballyv;

    let bt = btop - 12;
    let br = bleft + 12;
    let bb = btop + 12;
    let bl = bleft - 12;

    let bricks = [...this.refs.block.childNodes]
      .map(brick => brick.getClientRects()[0])
      .filter((brick, index) => this.props.store.bricks[index].alive);
    let actbricks = this.props.store.bricks.filter(brick => brick.alive);

    console.log(this.props.store.Hitsmtng);
    if (!this.props.store.Hitsmtng) {
      bricks.forEach((brick, index) => {
        if (
          bt <= brick.bottom + bv * byv &&
          bb >= brick.top - bv * byv &&
          br >= brick.left - bv * bxv &&
          bl <= brick.right + bv * bxv
        ) {
          this.props.dispatch(BrickHit(actbricks[index].id));
          if (bt <= brick.bottom + bv * byv && bb >= brick.top - bv * byv) {
            byv *= -1;
            this.props.dispatch(ChnageYVel(-1));
            if (bt <= brick.bottom && bb > brick.bottom) {
              btop = brick.bottom + 12;
            } else if (bb >= brick.top && bt < brick.top) {
              btop = brick.top - 12;
            }
          } else if (
            br >= brick.left - bv * bxv &&
            bl <= brick.right + bv * bxv
          ) {
            bxv *= -1;
            this.props.dispatch(ChnageXVel(-1));
            if (br >= brick.left && bl < brick.left) {
              bleft = brick.left - 12;
            } else if (bl <= brick.right && br > brick.right) {
              bleft = brick.right + 12;
            }
          }
          bv = 0;
          this.props.dispatch(ChnageVel(0));
          this.props.dispatch(ToggleHit());
        }
      });
      if (bt <= bv * byv || bb >= canvas.height + bv * byv) {
        if (bt <= bv * byv) {
          btop = 12;
        } else if (bb >= canvas.height + bv * byv) {
          btop = canvas.height - 12;
        }
        byv *= -1;
        this.props.dispatch(ChnageYVel(-1));
        bv = 0;
        this.props.dispatch(ChnageVel(0));
        this.props.dispatch(ToggleHit());
      }
      if (bl <= -bv * bxv || br >= canvas.width - bv * bxv) {
        if (bl <= -bv * bxv) {
          bleft = 12;
        } else if (br >= canvas.width - bv * bxv) {
          bleft = canvas.width - 12;
        }
        bxv *= -1;
        this.props.dispatch(ChnageXVel(-1));
        bv = 0;
        this.props.dispatch(ChnageVel(0));
        this.props.dispatch(ToggleHit());
      }
    } else {
      this.props.dispatch(ToggleHit());
      this.props.dispatch(ChangeVel(6));
    }

    let x = bleft + bv * bxv;
    let y = btop - bv * byv;

    this.props.dispatch(ChangeCoords(x, y));

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.fillRect(x - 12, y - 12, 24, 24);
  };
  ballStop = () => {
    clearInterval(window.moving);
  };
  render() {
    return (
      <div
        className={this.props.store.game ? "form breakout" : "form"}
        ref="game"
      >
        <h1>Now Enough About Me...</h1>
        <form>
          <div className="inputs">
            <input type="text" placeholder="First Name" />
            <input type="text" placeholder="Last Name" />
            <input type="email" placeholder="Your Email" />
            <input type="text" placeholder="Subject" />
          </div>
          <div className="gamearea">
            <div className="input__block">
              <div ref="block" className="block">
                {this.props.store.bricks.map(brick => {
                  let classname = "brick ";
                  classname += brick.alive ? "" : "dead";
                  return <div key={brick.id} className={classname} />;
                })}
              </div>
              <textarea placeholder="Your Lovely Message Goes Here :)" />
            </div>
            {/* <div
              class="g-recaptcha"
              data-sitekey="6LdC230UAAAAAHrGqrEH6yvebju1qIz3XxF4jsfW"
            /> */}
            {/* <div className="hcit">
              <h1>HOW COOL IS THAT?</h1>
            </div> */}
            <canvas ref="canvas" />
            <div className="input__bar">
              <div ref="bar" className="bar" />
              <button>Submit</button>
            </div>
          </div>
        </form>
        <div className="toggleGame">
          <button ref="toggleGame" onClick={this.toggleGameplay}>
            Game!
          </button>
        </div>
      </div>
    );
  }
}

const Form = connect(mapStateToProps)(ToConnectForm);

export default Form;
