import React, { Component } from "react";
import { connect } from "react-redux";

import { toggleGameplay, toggleBarReady } from "../store/actions/actions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

class ToConnectForm extends Component {
  componentDidMount() {
    this.refs.game.addEventListener("mousemove", e => {
      this.handleMouseMove(e);
    });
    this.refs.block.childNodes[3].classList.add("dead");
  }
  handleMouseMove = e => {
    if (this.props.store.barReady) {
      if (e.clientX >= 115 && e.clientX <= window.innerWidth - 115) {
        this.refs.bar.style.left = e.clientX + "px";
      } else if (e.clientX < 115) {
        this.refs.bar.style.left = 115 + "px";
      } else if (e.clientX > window.innerWidth - 115) {
        this.refs.bar.style.left = window.innerWidth - 115 + "px";
      }
    }
  };
  toggleGameplay = () => {
    if (!this.props.store.game) {
      this.props.dispatch(toggleGameplay());
      setTimeout(() => {
        this.refs.block.style.backgroundColor = "black";
        this.props.dispatch(toggleBarReady());
      }, 4000);
    } else {
      this.props.dispatch(toggleBarReady());

      this.refs.bar.style.transition = "opacity 1s, left 1s";
      this.refs.bar.style.left = window.innerWidth / 2 + "px";

      this.refs.block.classList.add("blocktrans");

      this.refs.block.childNodes.forEach(brick => {
        brick.classList.add("bricktrans");
      });

      setTimeout(() => {
        this.refs.bar.style.transition = "opacity 1s";
        this.refs.block.classList.remove("blocktrans");
        this.refs.block.childNodes.forEach(brick => {
          brick.classList.remove("bricktrans");
        });
      }, 1000);

      setTimeout(() => {
        this.props.dispatch(toggleGameplay());
      }, 1000);
    }
  };
  render() {
    return (
      <div
        className={this.props.store.game ? "form breakout" : "form"}
        ref="game"
      >
        <h1>Now Enough About Me...</h1>
        <form>
          <div className="inputs">
            <input type="text" placeholder="First Name" />
            <input type="text" placeholder="Last Name" />
            <input type="email" placeholder="Your Email" />
            <input type="text" placeholder="Subject" />
          </div>
          <div className="gamearea">
            <div className="input__block">
              <div ref="block" className="block">
                {this.props.store.bricks.map(brick => {
                  let classname = "brick ";
                  classname += brick.alive ? "" : "dead";
                  return <div key={brick.id} className={classname} />;
                })}
              </div>
              <textarea placeholder="Your Lovely Message Goes Here :)" />
            </div>
            {/* <div
              class="g-recaptcha"
              data-sitekey="6LdC230UAAAAAHrGqrEH6yvebju1qIz3XxF4jsfW"
            /> */}
            {/* <div className="hcit">
              <h1>HOW COOL IS THAT?</h1>
            </div> */}
            <div className="input__bar">
              <div ref="bar" className="bar" />
              <button>Submit</button>
            </div>
            <div className="ball" />
          </div>
        </form>
        <div className="toggleGame">
          <button onClick={this.toggleGameplay}>Game!</button>
        </div>
      </div>
    );
  }
}

const Form = connect(mapStateToProps)(ToConnectForm);

export default Form;
