import React, { Component } from "react";
import { connect } from "react-redux";

import { BrowserRouter, Route, Switch, withRouter } from "react-router-dom";

import Home from "./components/pages/home";
import About from "./components/pages/about";
import Portfolio from "./components/pages/portfolio";
import Contact from "./components/pages/contact";
import Error from "./components/pages/errorpage";

import Navigation from "./components/layout/navigation";
import { PageTurn } from "./store/actions/pageactions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

const RoutedNavigation = withRouter(props => <Navigation {...props} />);

class ToConnectApp extends Component {
  componentDidMount() {
    document.addEventListener("wheel", this.handleScroll);
  }
  handleScroll = e => {
    let pages = ["home", "about", "portfolio", "contact"];
    let currpage = this.props.store.page.page;
    let currindex = pages.indexOf(currpage);
    if (e.deltaY >= 0) {
      if (currpage !== "contact") {
        this.navigate(pages[currindex + 1], currindex + 1);
      }
    } else if (e.deltaY < 0) {
      if (currpage !== "home") {
        this.navigate(pages[currindex - 1], currindex - 1);
      }
    }
  };
  navigate = (page, index) => {
    document.removeEventListener("wheel", this.handleScroll);
    if (
      this.props.store.page.page === "contact" &&
      this.props.store.form.infoprovided
    ) {
      let confirmed = window.confirm(
        `If You Continue, All Your Inputs Will Be Erased.\nIs That OK?`
      );
      if (confirmed) {
        this.pageturn(page, index);
      }
    } else {
      this.pageturn(page, index);
    }
    document.addEventListener("wheel", this.handleScroll);
  };
  pageturn = (page, index) => {
    if (this.props.store.page.page !== page) {
      let buttons = this.refs.display.childNodes[0].childNodes[0].childNodes;
      buttons.forEach(btn => (btn.disabled = true));

      let links = this.refs.display.childNodes[0].childNodes[1].childNodes;
      links.forEach(link => link.classList.remove("active"));

      document.getElementsByTagName("body")[0].style.overflow = "hidden";
      this.refs.display.classList.add("pagegoingupfor");

      window.pagegoup = setTimeout(() => {
        this.props.dispatch(PageTurn(page));
        links[index].classList.add("active");
        links[index].click();

        this.refs.display.classList.remove("pagegoingupfor");
        this.refs.display.classList.add("pagegoingdownfor");
        window.pagegodown = setTimeout(() => {
          this.refs.display.classList.remove("pagegoingdownfor");
          document.getElementsByTagName("body")[0].style.overflow = "";

          buttons.forEach(btn => (btn.disabled = false));
        }, 1000);
      }, 1000);
    }
  };
  render() {
    return (
      <div className="App">
        <div className="loading" />
        <BrowserRouter>
          <div ref="display" className="display">
            <RoutedNavigation
              pageturn={this.pageturn}
              navigate={this.navigate}
            />
            <Switch>
              <Route path="/" component={Home} exact />
              <Route path="/about" component={About} />
              <Route path="/portfolio" component={Portfolio} />
              <Route path="/contact" component={Contact} />
              <Route component={Error} />
            </Switch>
          </div>
        </BrowserRouter>
      </div>
    );
  }
}

const App = connect(mapStateToProps)(ToConnectApp);

export default App;
