import React, { Component } from "react";
import { connect } from "react-redux";

import { BrowserRouter, Route, Switch, withRouter } from "react-router-dom";

import Home from "./components/pages/home";
import About from "./components/pages/about";
import Portfolio from "./components/pages/portfolio";
import Contact from "./components/pages/contact";
import Error from "./components/pages/errorpage";

import Navigation from "./components/layout/navigation";
import NaviDisplay from "./components/layout/navidisplay";

import { PageTurn } from "./store/actions/pageactions";
import { ResetStore, ResetBricks } from "./store/actions/formactions";

const mapStateToProps = state => {
  return {
    store: state
  };
};

const RoutedNavigation = withRouter(props => <Navigation {...props} />);

class ToConnectApp extends Component {
  componentDidMount() {
    setTimeout(() => {
      let pages = ["home", "about", "portfolio", "contact"];
      let currpage = pages.find(page => page === this.props.store.page.page);
      this.refs.app.childNodes[1].childNodes[0].childNodes[
        pages.indexOf(currpage)
      ].classList.add("active");
    }, 1);
  }
  navigate = (page, index) => {
    if (
      this.props.store.page.page === "contact" &&
      (this.props.store.form.game || this.props.store.form.infoprovided)
    ) {
      let allSet = true;

      let gameConfirmed;
      let formConfirmed;

      if (this.props.store.form.game) {
        gameConfirmed = window.confirm(
          `Are You Sure You Want To Exit The Game?`
        );
      } else {
        gameConfirmed = false;
      }
      if (!gameConfirmed && this.props.store.form.infoprovided) {
        formConfirmed = window.confirm(
          `If You Continue, All Your Inputs Will Be Erased.\nIs That OK?`
        );
      }

      let confirmed = window.confirm(`Are You Sure You Want To Exit The Game?`);
      if (confirmed) {
        this.pageturn(page, index);
      }
    } else if (
      this.props.store.page.page === "contact" &&
      this.props.store.form.infoprovided
    ) {
      let confirmed = window.confirm(
        `If You Continue, All Your Inputs Will Be Erased.\nIs That OK?`
      );
      if (confirmed) {
        this.pageturn(page, index);
      }
    } else {
      this.pageturn(page, index);
    }
  };
  pageturn = (page, index) => {
    if (this.props.store.page.page !== page) {
      var killId = setTimeout(function() {
        for (var i = killId; i > 0; i--) clearInterval(i);
      }, 12);

      let buttons = this.refs.display.childNodes[0].childNodes[0].childNodes;
      buttons.forEach(btn => (btn.disabled = true));

      let links = this.refs.display.childNodes[0].childNodes[1].childNodes;
      links.forEach(link => link.classList.remove("active"));

      let navdisp = this.refs.app.childNodes[1];
      navdisp.style.transform = "translateX(0%)";

      document.getElementsByTagName("body")[0].style.overflow = "hidden";
      this.refs.display.classList.add("pagegoingupfor");

      window.pagegoup = setTimeout(() => {
        let navdisplinks = navdisp.childNodes[0].childNodes;
        navdisplinks.forEach(ndlink => ndlink.classList.remove("active"));
        navdisplinks[index].classList.add("active");

        this.props.dispatch(PageTurn(page));
        links[index].classList.add("active");
        links[index].click();

        this.refs.display.classList.remove("pagegoingupfor");
        this.refs.display.classList.add("pagegoingdownfor");
        window.pagegodown = setTimeout(() => {
          this.refs.display.classList.remove("pagegoingdownfor");
          document.getElementsByTagName("body")[0].style.overflow = "";

          this.props.dispatch(ResetStore());
          this.props.dispatch(ResetBricks());

          navdisp.style.transform = "translateX(100%)";

          buttons.forEach(btn => (btn.disabled = false));
        }, 1000);
      }, 1000);
    }
  };
  playGame = () => {
    this.navigate("contact", 3);
    setTimeout(() => {
      this.refs.app.childNodes[2].childNodes[1].childNodes[0].childNodes[2].childNodes[0].click();
    }, 3000);
  };
  render() {
    return (
      <div ref="app" className="App">
        <div className="loading" />
        <NaviDisplay />
        <BrowserRouter>
          <div ref="display" className="display">
            <RoutedNavigation
              pageturn={this.pageturn}
              navigate={this.navigate}
            />
            <Switch>
              <Route
                path="/"
                render={() => (
                  <Home navigate={this.navigate} playGame={this.playGame} />
                )}
                exact
              />
              <Route
                path="/about"
                render={() => <About navigate={this.navigate} />}
              />
              <Route
                path="/portfolio"
                render={() => <Portfolio navigate={this.navigate} />}
              />
              <Route path="/contact" component={Contact} />
              <Route component={Error} />
            </Switch>
          </div>
        </BrowserRouter>
      </div>
    );
  }
}

const App = connect(mapStateToProps)(ToConnectApp);

export default App;
