import React, { Component } from "react";

import Skills from "../about/skills";

class About extends Component {
  componentDidMount() {
    document.addEventListener("scroll", this.handleScroll);
  }
  componentWillUnmount() {
    document.removeEventListener("scroll", this.handleScroll);
  }
  handleScroll = e => {
    console.log(window.offsetHeight);
    if (
      window.scrollY + window.offsetHeight / 2 >=
      this.refs.about.childNodes[2].offsetTop
    ) {
      this.brainIntro();
      document.removeEventListener("scroll", this.handleScroll);
    }
  };
  brainIntro = () => {
    let braintemplate = this.refs.about.childNodes[2].childNodes[0]
      .childNodes[0];
    let brainparts = this.refs.about.childNodes[2].childNodes[0].childNodes[1];

    console.log(braintemplate, brainparts);
  };
  render() {
    return (
      <div ref="about" className="about">
        <div className="about__me">
          <h1>About Me</h1>

          <h2>Who Am I?</h2>
          <p>
            Unless you've directly landed here skipping the homepage my name is
            Aman Kumar (Pronounced "Aam-un Koo-maar"). I am a Front End Web
            Developer from India. I spend 12 hours a day in front of my
            computer. 8 hours to code, 2 hours for browse reddit and the rest to
            play video games. You can call me a workaholic. I love building
            projects for the web and just programming in general. My source of
            energy is my willpower, commitment, a little bit of stubbornness and
            a lot of coffee.
          </p>

          <h2>So What Exactly Do I Do?</h2>
          <p>
            We already know that I'm a web developer, I build websites and web
            applications, but does a filmmaker just shoot vidoes? and a
            photographer just capture images? Well yeah, but my point is, I love
            my work. I take it very seriously and look at it as a lot more than
            just sitting on a desk and spewing out code. I don't just build
            websites, I build Experiences.
          </p>

          <h2>I Like My Websites...</h2>
          <div className="ilmw">
            <div className="ilmw--item">
              <h3>Fast</h3>
              <p>Every Millisecond Counts.</p>
            </div>
            <div className="ilmw--item">
              <h3>Responsive</h3>
              <p>Desktop, Mobile, Tablet, 65 inch TV, etc...</p>
            </div>
            <div className="ilmw--item">
              <h3>Dynamic</h3>
              <p>Nobody Likes Their Website To Be Static.</p>
            </div>
            <div className="ilmw--item">
              <h3>Up To Date</h3>
              <p>Only The Latest And Best Technologies Used.</p>
            </div>
            <div className="ilmw--item">
              <h3>Compatible</h3>
              <p>Supported By Almost All Browsers. No One Left Behind.</p>
            </div>
          </div>
        </div>

        <h2>Technologies I Use</h2>
        <Skills />
        <div className="about__footer">
          <div className="about__footer--contact">
            <h1>Convinced?</h1>
            <button onClick={() => this.props.navigate("contact", 3)}>
              Contact Me
            </button>
          </div>
          <div className="about__footer--portfolio">
            <h1>Shut Up And Show Me The Code</h1>
            <button onClick={() => this.props.navigate("portfolio", 2)}>
              See My Projects
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default About;
